mapper.select:
		select * from record_hour_dhcp


                order by ${sidx} ${order}


                order by id desc



			limit #{offset}, #{limit}

select record_hour_dhcp.id,record_hour_dhcp.city_id,record_hour_dhcp.county_id,record_hour_dhcp.probe_id,record_hour_dhcp.port,record_hour_dhcp.task_id,record_hour_dhcp.service_type,record_hour_dhcp.target_id,record_hour_dhcp.target_ip,record_hour_dhcp.record_date,record_hour_dhcp.remark, city.city_name AS city_name, county.county_name AS area_name, probe.probe_name AS probe_name, probe.access_layer, target.target_name AS target_name, time_format(record_time, '%H') AS record_time, AVG(record_hour_dhcp.delay) AS delay, AVG(record_hour_dhcp.success_rate) AS success_rate, SUM(record_hour_dhcp.fail) AS fail, SUM(record_hour_dhcp.total) AS total FROM record_hour_dhcp LEFT JOIN city ON record_hour_dhcp.city_id = city.city_id LEFT JOIN county ON record_hour_dhcp.county_id = county.county_id INNER JOIN probe ON record_hour_dhcp.probe_id = probe.id LEFT JOIN target ON record_hour_dhcp.target_id = target.id WHERE record_hour_dhcp.record_date BETWEEN #{ava_start} AND #{ava_terminal}
AND record_hour_dhcp.city_id = #{city_id}
AND record_hour_dhcp.county_id = #{county_id}
AND record_hour_dhcp.target_id = #{target_id}
AND record_hour_dhcp.probe_id = #{probe_id}
GROUP BY record_hour_dhcp.city_id,record_hour_dhcp.county_id,record_hour_dhcp.service_type,record_hour_dhcp.target_id,record_hour_dhcp.probe_id,date_format(record_hour_dhcp.record_date,'%Y-%m-%d'),time_format(record_hour_dhcp.record_time, '%H')

select dhcp.id,dhcp.city_id,dhcp.county_id,dhcp.probe_id,dhcp.port,dhcp.task_id,dhcp.service_type,dhcp.target_id,dhcp.target_ip,dhcp.record_date,dhcp.remark, city.city_name AS city_name, county.county_name AS area_name, probe.probe_name AS probe_name, target.target_name AS target_name, time_format(record_time, '%H') AS record_time, AVG(dhcp.delay) AS delay, AVG(dhcp.success_rate) AS success_rate, SUM(dhcp.fail) AS fail, SUM(dhcp.total) AS total FROM record_day_dhcp dhcp LEFT JOIN city ON dhcp.city_id = city.city_id LEFT JOIN county ON dhcp.county_id = county.county_id INNER JOIN probe ON dhcp.probe_id = probe.id LEFT JOIN target ON dhcp.target_id = target.id WHERE dhcp.record_date BETWEEN #{ava_start} AND #{ava_terminal}
AND dhcp.city_id = #{city_id}
AND dhcp.county_id = #{county_id}
AND dhcp.target_id = #{target_id}
AND dhcp.probe_id = #{probe_id}
GROUP BY dhcp.city_id,dhcp.county_id,dhcp.service_type,dhcp.target_id,dhcp.probe_id,date_format(dhcp.record_date,'%Y-%m-%d'),time_format(dhcp.record_time, '%H')

select rhp.id,rhp.probe_id,rhp.port,rhp.task_id,rhp.service_type,rhp.target_id,rhp.target_ip,rhp.record_date,rhp.remark, probe.probe_name AS probe_name, task.task_name AS task_name, target.target_name AS target_name, target.value AS targetip_name, floor(time_format(record_time, '%H')/#{interval}) AS rt, concat('从',floor(time_format(record_time, '%H')/#{interval})*#{interval},'点至',floor(time_format(record_time, '%H')/#{interval})*#{interval}+#{interval},'点') AS record_time, round(AVG(rhp.delay),2) AS delay, round(AVG(rhp.success_rate),2) AS success_rate, d.data_name AS servicetype_name FROM record_hour_dhcp rhp INNER JOIN probe ON rhp.probe_id = probe.id INNER JOIN task ON rhp.task_id = task.id INNER JOIN target ON rhp.target_id = target.id LEFT JOIN dic_data d ON (d.dic_type_id = 5 AND rhp.service_type = d.data_id) WHERE rhp.service_type = #{service_type} AND rhp.record_date BETWEEN #{startDate} AND #{terminalDate} AND rhp.record_time BETWEEN #{start_time} AND #{end_time}
AND rhp.task_id = #{task_id}
AND rhp.target_id = #{target_id}
AND rhp.probe_id = #{probe_id}
GROUP BY rhp.service_type,rhp.target_id,rhp.task_id,rhp.probe_id,date_format(rhp.record_date,'%Y-%m-%d'),rt
ORDER BY ${sidx} ${order}
ORDER BY record_date DESC,rt DESC
limit #{offset}, #{limit}

SELECT count(*) FROM (
select
rhp.id,rhp.probe_id,rhp.port,rhp.task_id,rhp.service_type,rhp.target_id,rhp.target_ip,rhp.record_date,rhp.remark,
floor(time_format(record_time, '%H')/#{interval}) AS rt
FROM record_hour_dhcp rhp
INNER JOIN probe
ON rhp.probe_id = probe.id
INNER JOIN task
ON rhp.task_id = task.id
INNER JOIN target
ON rhp.target_id = target.id
WHERE
rhp.service_type = #{service_type}
AND rhp.record_date BETWEEN #{startDate} AND #{terminalDate}
AND rhp.record_time BETWEEN #{start_time} AND #{end_time}

	AND rhp.task_id = #{task_id}


	AND rhp.target_id = #{target_id}


	AND rhp.probe_id = #{probe_id}

GROUP BY
rhp.service_type,rhp.target_id,rhp.task_id,rhp.probe_id,date_format(rhp.record_date,'%Y-%m-%d'),rt) t
