mapper.select:
		select * from record_hour_game


                order by ${sidx} ${order}


                order by id desc



			limit #{offset}, #{limit}

select record_hour_game.id,record_hour_game.city_id,record_hour_game.county_id,record_hour_game.probe_id,record_hour_game.port,record_hour_game.task_id,record_hour_game.service_type,record_hour_game.target_id,record_hour_game.target_ip,record_hour_game.record_date,record_hour_game.record_time,record_hour_game.remark, city.city_name AS city_name, county.county_name AS area_name, probe.probe_name AS probe_name, target.target_name AS target_name, AVG(record_hour_game.conn_delay) AS conn_delay, AVG(record_hour_game.packet_delay) AS packet_delay, AVG(record_hour_game.packet_jitter) AS packet_jitter, AVG(record_hour_game.loss_rate) AS loss_rate FROM record_hour_game LEFT JOIN city ON record_hour_game.city_id = city.city_id LEFT JOIN county ON record_hour_game.county_id = county.county_id INNER JOIN probe ON record_hour_game.probe_id = probe.id LEFT JOIN target ON record_hour_game.target_id = target.id WHERE record_hour_game.record_date BETWEEN #{ava_start} AND #{ava_terminal}
AND record_hour_game.city_id = #{city_id}
AND record_hour_game.county_id = #{county_id}
AND record_hour_game.target_id = #{target_id}
AND record_hour_game.probe_id = #{probe_id}
GROUP BY record_hour_game.city_id,record_hour_game.county_id,record_hour_game.service_type,record_hour_game.target_id,record_hour_game.probe_id,date_format(record_hour_game.record_date,'%Y-%m-%d'),time_format(record_hour_game.record_time, '%H')

select game.id,game.city_id,game.county_id,game.probe_id,game.port,game.task_id,game.service_type,game.target_id,game.target_ip,game.record_date,game.record_time,game.remark, city.city_name AS city_name, county.county_name AS area_name, probe.probe_name AS probe_name, target.target_name AS target_name, AVG(game.conn_delay) AS conn_delay, AVG(game.packet_delay) AS packet_delay, AVG(game.packet_jitter) AS packet_jitter, AVG(game.loss_rate) AS loss_rate FROM record_day_game game LEFT JOIN city ON game.city_id = city.city_id LEFT JOIN county ON game.county_id = county.county_id INNER JOIN probe ON game.probe_id = probe.id LEFT JOIN target ON game.target_id = target.id WHERE game.record_date BETWEEN #{ava_start} AND #{ava_terminal}
AND game.city_id = #{city_id}
AND game.county_id = #{county_id}
AND game.target_id = #{target_id}
AND game.probe_id = #{probe_id}
GROUP BY game.city_id,game.county_id,game.service_type,game.target_id,game.probe_id,date_format(game.record_date,'%Y-%m-%d'),time_format(game.record_time, '%H')

select rhp.id,rhp.probe_id,rhp.port,rhp.task_id,rhp.service_type,rhp.target_id,rhp.target_ip,rhp.record_date,rhp.remark, probe.probe_name AS probe_name, task.task_name AS task_name, target.target_name AS target_name, target.value AS targetip_name, floor(time_format(record_time, '%H')/#{interval}) AS rt, concat('从',floor(time_format(record_time, '%H')/#{interval})*#{interval},'点至',floor(time_format(record_time, '%H')/#{interval})*#{interval}+#{interval},'点') AS record_time, round(AVG(rhp.delay),2) AS delay, round(AVG(rhp.delay_std),2) AS delay_std, round(AVG(rhp.delay_var),2) AS delay_var, round(AVG(rhp.jitter),2) AS jitter, round(AVG(rhp.jitter_std),2) AS jitter_std, round(AVG(rhp.jitter_var),2) AS jitter_var, round(AVG(rhp.loss_rate),2) AS loss_rate, d.data_name AS servicetype_name FROM record_hour_game rhp INNER JOIN probe ON rhp.probe_id = probe.id INNER JOIN task ON rhp.task_id = task.id INNER JOIN target ON rhp.target_id = target.id LEFT JOIN dic_data d ON (d.dic_type_id = 5 AND rhp.service_type = d.data_id) WHERE rhp.service_type = #{service_type} AND rhp.record_date BETWEEN #{startDate} AND #{terminalDate} AND rhp.record_time BETWEEN #{start_time} AND #{end_time}
AND rhp.task_id = #{task_id}
AND rhp.target_id = #{target_id}
AND rhp.probe_id = #{probe_id}
GROUP BY rhp.service_type,rhp.target_id,rhp.task_id,rhp.probe_id,date_format(rhp.record_date,'%Y-%m-%d'),rt
ORDER BY ${sidx} ${order}
ORDER BY record_date DESC,rt DESC
limit #{offset}, #{limit}

SELECT count(*) FROM (
select
rhp.id,rhp.probe_id,rhp.port,rhp.task_id,rhp.service_type,rhp.target_id,rhp.target_ip,rhp.record_date,rhp.remark,
floor(time_format(record_time, '%H')/#{interval}) AS rt,AVG(rhp.delay) AS delay,
AVG(rhp.delay_std) AS delay_std,
AVG(rhp.delay_var) AS delay_var,
AVG(rhp.jitter) AS jitter,
AVG(rhp.jitter_std) AS jitter_std,
AVG(rhp.jitter_var) AS jitter_var,
AVG(rhp.loss_rate) AS loss_rate
FROM record_hour_game rhp
WHERE
rhp.service_type = #{service_type}
AND rhp.record_date BETWEEN #{startDate} AND #{terminalDate}
AND rhp.record_time BETWEEN #{start_time} AND #{end_time}

	AND rhp.task_id = #{task_id}


	AND rhp.target_id = #{target_id}


	AND rhp.probe_id = #{probe_id}

GROUP BY
rhp.service_type,rhp.target_id,rhp.task_id,rhp.probe_id,date_format(rhp.record_date,'%Y-%m-%d'),rt) t
