mapper.select:
		select * from record_hour_pppoe


                order by ${sidx} ${order}


                order by id desc



			limit #{offset}, #{limit}

select record_hour_pppoe.id,record_hour_pppoe.city_id,record_hour_pppoe.county_id,record_hour_pppoe.probe_id,record_hour_pppoe.port,record_hour_pppoe.task_id,record_hour_pppoe.service_type,record_hour_pppoe.target_id,record_hour_pppoe.target_ip,record_hour_pppoe.record_date,record_hour_pppoe.record_time,record_hour_pppoe.remark, city.city_name AS city_name, county.county_name AS area_name, probe.probe_name AS probe_name, target.target_name AS target_name, AVG(record_hour_pppoe.delay) AS delay, AVG(record_hour_pppoe.drop_rate) AS drop_rate, AVG(record_hour_pppoe.success_rate) AS success_rate, SUM(record_hour_pppoe.fail) AS fail, SUM(record_hour_pppoe.total) AS total FROM record_hour_pppoe LEFT JOIN city ON record_hour_pppoe.city_id = city.city_id LEFT JOIN county ON record_hour_pppoe.county_id = county.county_id INNER JOIN probe ON record_hour_pppoe.probe_id = probe.id LEFT JOIN target ON record_hour_pppoe.target_id = target.id WHERE record_hour_pppoe.record_date BETWEEN #{ava_start} AND #{ava_terminal}
AND record_hour_pppoe.city_id = #{city_id}
AND record_hour_pppoe.county_id = #{county_id}
AND record_hour_pppoe.target_id = #{target_id}
AND record_hour_pppoe.probe_id = #{probe_id}
GROUP BY record_hour_pppoe.city_id,record_hour_pppoe.county_id,record_hour_pppoe.service_type,record_hour_pppoe.target_id,record_hour_pppoe.probe_id,date_format(record_hour_pppoe.record_date,'%Y-%m-%d'),time_format(record_hour_pppoe.record_time, '%H')

select pppoe.id,pppoe.city_id,pppoe.county_id,pppoe.probe_id,pppoe.port,pppoe.task_id,pppoe.service_type,pppoe.target_id,pppoe.target_ip,pppoe.record_date,pppoe.record_time,pppoe.remark, city.city_name AS city_name, county.county_name AS area_name, probe.probe_name AS probe_name, target.target_name AS target_name, AVG(pppoe.delay) AS delay, AVG(pppoe.drop_rate) AS drop_rate, AVG(pppoe.success_rate) AS success_rate, SUM(pppoe.fail) AS fail, SUM(pppoe.total) AS total FROM record_day_pppoe pppoe LEFT JOIN city ON pppoe.city_id = city.city_id LEFT JOIN county ON pppoe.county_id = county.county_id INNER JOIN probe ON pppoe.probe_id = probe.id LEFT JOIN target ON pppoe.target_id = target.id WHERE pppoe.record_date BETWEEN #{ava_start} AND #{ava_terminal}
AND pppoe.city_id = #{city_id}
AND pppoe.county_id = #{county_id}
AND pppoe.target_id = #{target_id}
AND pppoe.probe_id = #{probe_id}
GROUP BY pppoe.city_id,pppoe.county_id,pppoe.service_type,pppoe.target_id,pppoe.probe_id,date_format(pppoe.record_date,'%Y-%m-%d'),time_format(pppoe.record_time, '%H')

select rhp.id,rhp.probe_id,rhp.port,rhp.task_id,rhp.service_type,rhp.target_id,rhp.target_ip,rhp.record_date,rhp.remark, probe.probe_name AS probe_name, task.task_name AS task_name, target.target_name AS target_name, target.value AS targetip_name, floor(time_format(record_time, '%H')/#{interval}) AS rt, concat('从',floor(time_format(record_time, '%H')/#{interval})*#{interval},'点至',floor(time_format(record_time, '%H')/#{interval})*#{interval}+#{interval},'点') AS record_time, round(AVG(rhp.delay),2) AS delay, round(AVG(rhp.drop_rate),2) AS drop_rate, round(AVG(rhp.success_rate),2) AS success_rate, d.data_name AS servicetype_name FROM record_hour_pppoe rhp INNER JOIN probe ON rhp.probe_id = probe.id INNER JOIN task ON rhp.task_id = task.id INNER JOIN target ON rhp.target_id = target.id LEFT JOIN dic_data d ON (d.dic_type_id = 5 AND rhp.service_type = d.data_id) WHERE rhp.service_type = #{service_type} AND rhp.record_date BETWEEN #{startDate} AND #{terminalDate} AND rhp.record_time BETWEEN #{start_time} AND #{end_time}
AND rhp.task_id = #{task_id}
AND rhp.target_id = #{target_id}
AND rhp.probe_id = #{probe_id}
GROUP BY rhp.service_type,rhp.target_id,rhp.task_id,rhp.probe_id,date_format(rhp.record_date,'%Y-%m-%d'),rt
ORDER BY ${sidx} ${order}
ORDER BY record_date DESC,rt DESC
limit #{offset}, #{limit}

SELECT count(*) FROM (
select
rhp.id,rhp.probe_id,rhp.port,rhp.task_id,rhp.service_type,rhp.target_id,rhp.target_ip,rhp.record_date,rhp.remark,
floor(time_format(record_time, '%H')/#{interval}) AS rt,AVG(rhp.delay) AS delay
FROM record_hour_pppoe rhp
WHERE
rhp.service_type = #{service_type}
AND rhp.record_date BETWEEN #{startDate} AND #{terminalDate}
AND rhp.record_time BETWEEN #{start_time} AND #{end_time}

	AND rhp.task_id = #{task_id}


	AND rhp.target_id = #{target_id}


	AND rhp.probe_id = #{probe_id}

GROUP BY
rhp.service_type,rhp.target_id,rhp.task_id,rhp.probe_id,date_format(rhp.record_date,'%Y-%m-%d'),rt) t
