mapper.select:
		select * from record_hour_sla


                order by ${sidx} ${order}


                order by id desc



			limit #{offset}, #{limit}

select record_hour_sla.id,record_hour_sla.city_id,record_hour_sla.county_id,record_hour_sla.probe_id,record_hour_sla.port,record_hour_sla.task_id,record_hour_sla.service_type,record_hour_sla.target_id,record_hour_sla.target_ip,record_hour_sla.record_date,record_hour_sla.record_time,record_hour_sla.remark, city.city_name AS city_name, county.county_name AS area_name, probe.probe_name AS probe_name, target.target_name AS target_name, AVG(record_hour_sla.delay) AS delay, AVG(record_hour_sla.g_delay) AS g_delay, AVG(record_hour_sla.r_delay) AS r_delay, AVG(record_hour_sla.jitter) AS jitter, AVG(record_hour_sla.g_jitter) AS g_jitter, AVG(record_hour_sla.r_jitter) AS r_jitter, AVG(record_hour_sla.loss_rate) AS loss_rate, SUM(record_hour_sla.fail) AS fail, SUM(record_hour_sla.total) AS total FROM record_hour_sla LEFT JOIN city ON record_hour_sla.city_id = city.city_id LEFT JOIN county ON record_hour_sla.county_id = county.county_id INNER JOIN probe ON record_hour_sla.probe_id = probe.id LEFT JOIN target ON record_hour_sla.target_id = target.id WHERE record_hour_sla.record_date BETWEEN #{ava_start} AND #{ava_terminal}
AND record_hour_sla.city_id = #{city_id}
AND record_hour_sla.county_id = #{county_id}
AND record_hour_sla.target_id = #{target_id}
AND record_hour_sla.probe_id = #{probe_id}
GROUP BY record_hour_sla.city_id,record_hour_sla.county_id,record_hour_sla.service_type,record_hour_sla.target_id,record_hour_sla.probe_id,date_format(record_hour_sla.record_date,'%Y-%m-%d'),time_format(record_hour_sla.record_time, '%H')

select sla.id,sla.city_id,sla.county_id,sla.probe_id,sla.port,sla.task_id,sla.service_type,sla.target_id,sla.target_ip,sla.record_date,sla.record_time,sla.remark, city.city_name AS city_name, county.county_name AS area_name, probe.probe_name AS probe_name, target.target_name AS target_name, AVG(sla.delay) AS delay, AVG(sla.g_delay) AS g_delay, AVG(sla.r_delay) AS r_delay, AVG(sla.jitter) AS jitter, AVG(sla.g_jitter) AS g_jitter, AVG(sla.r_jitter) AS r_jitter, AVG(sla.loss_rate) AS loss_rate, SUM(sla.fail) AS fail, SUM(sla.total) AS total FROM record_day_sla sla LEFT JOIN city ON sla.city_id = city.city_id LEFT JOIN county ON sla.county_id = county.county_id INNER JOIN probe ON sla.probe_id = probe.id LEFT JOIN target ON sla.target_id = target.id WHERE sla.record_date BETWEEN #{ava_start} AND #{ava_terminal}
AND sla.city_id = #{city_id}
AND sla.county_id = #{county_id}
AND sla.target_id = #{target_id}
AND sla.probe_id = #{probe_id}
GROUP BY sla.city_id,sla.county_id,sla.service_type,sla.target_id,sla.probe_id,date_format(sla.record_date,'%Y-%m-%d'),time_format(sla.record_time, '%H')

select rhp.id,rhp.probe_id,rhp.port,rhp.task_id,rhp.service_type,rhp.target_id,rhp.target_ip,rhp.record_date,rhp.remark, probe.probe_name AS probe_name, task.task_name AS task_name, target.target_name AS target_name, target.value AS targetip_name, floor(time_format(record_time, '%H')/#{interval}) AS rt, concat('从',floor(time_format(record_time, '%H')/#{interval})*#{interval},'点至',floor(time_format(record_time, '%H')/#{interval})*#{interval}+#{interval},'点') AS record_time, round(AVG(rhp.delay),2) AS delay, round(AVG(rhp.delay_std),2) AS delay_std, round(AVG(rhp.delay_var),2) AS delay_var, round(AVG(rhp.jitter),2) AS jitter, round(AVG(rhp.jitter_std),2) AS jitter_std, round(AVG(rhp.jitter_var),2) AS jitter_var, round(AVG(rhp.loss_rate),2) AS loss_rate, d.data_name AS servicetype_name FROM record_hour_sla rhp INNER JOIN probe ON rhp.probe_id = probe.id INNER JOIN task ON rhp.task_id = task.id INNER JOIN target ON rhp.target_id = target.id LEFT JOIN dic_data d ON (d.dic_type_id = 5 AND rhp.service_type = d.data_id) WHERE rhp.service_type = #{service_type} AND rhp.record_date BETWEEN #{startDate} AND #{terminalDate} AND rhp.record_time BETWEEN #{start_time} AND #{end_time}
AND rhp.task_id = #{task_id}
AND rhp.target_id = #{target_id}
AND rhp.probe_id = #{probe_id}
GROUP BY rhp.service_type,rhp.target_id,rhp.task_id,rhp.probe_id,date_format(rhp.record_date,'%Y-%m-%d'),rt
ORDER BY ${sidx} ${order}
ORDER BY record_date DESC,rt DESC
limit #{offset}, #{limit}

SELECT count(*) FROM (
select
rhp.id,rhp.probe_id,rhp.port,rhp.task_id,rhp.service_type,rhp.target_id,rhp.target_ip,rhp.record_date,rhp.remark,
floor(time_format(record_time, '%H')/#{interval}) AS rt,AVG(rhp.delay) AS delay,
AVG(rhp.delay_std) AS delay_std,
AVG(rhp.delay_var) AS delay_var,
AVG(rhp.jitter) AS jitter,
AVG(rhp.jitter_std) AS jitter_std,
AVG(rhp.jitter_var) AS jitter_var,
AVG(rhp.loss_rate) AS loss_rate
FROM record_hour_sla rhp
WHERE
rhp.service_type = #{service_type}
AND rhp.record_date BETWEEN #{startDate} AND #{terminalDate}
AND rhp.record_time BETWEEN #{start_time} AND #{end_time}

	AND rhp.task_id = #{task_id}


	AND rhp.target_id = #{target_id}


	AND rhp.probe_id = #{probe_id}

GROUP BY
rhp.service_type,rhp.target_id,rhp.task_id,rhp.probe_id,date_format(rhp.record_date,'%Y-%m-%d'),rt) t
