mapper.select:
		select * from record_hour_tracert


                order by ${sidx} ${order}


                order by id desc



			limit #{offset}, #{limit}

SELECT t.*, city.city_name AS city_name, county.county_name AS area_name, probe.probe_name AS probe_name, probe.access_layer, target.target_name AS target_name FROM (select rht.id,rht.city_id,rht.county_id,rht.probe_id,rht.port,rht.task_id,rht.service_type,rht.target_id,rht.target_ip,rht.record_date,rht.remark, time_format(record_time, '%H') AS record_time, AVG(rht.delay) AS delay, AVG(rht.delay_std) AS delay_std, AVG(rht.delay_var) AS delay_var, AVG(rht.jitter) AS jitter, AVG(rht.jitter_std) AS jitter_std, AVG(rht.jitter_var) AS jitter_var, AVG(rht.loss_rate) AS loss_rate, SUM(rht.fail) AS fail, SUM(rht.total) AS total FROM record_hour_tracert rht force index(idx_score) WHERE rht.record_date BETWEEN #{ava_start} AND #{ava_terminal}
AND rht.probe_id = #{probe_id}
AND rht.city_id = #{city_id}
AND rht.county_id = #{county_id}
AND rht.target_id = #{target_id}
AND record_time BETWEEN #{startTime} AND #{terminalTime}
GROUP BY rht.record_date,rht.probe_id,rht.city_id,rht.county_id,rht.record_time,rht.service_type)t LEFT JOIN city ON t.city_id = city.city_id LEFT JOIN county ON t.county_id = county.county_id INNER JOIN probe ON t.probe_id = probe.id INNER JOIN target ON t.target_id = target.id
order by ${sidx} ${order}
limit #{offset}, #{limit}

SELECT t.*, city.city_name AS city_name, county.county_name AS area_name, probe.probe_name AS probe_name, probe.access_layer, target.target_name AS target_name FROM (select rht.id,rht.city_id,rht.county_id,rht.probe_id,rht.port,rht.task_id,rht.service_type,rht.target_id,rht.target_ip,rht.record_date,rht.remark, time_format(record_time, '%H') AS record_time, AVG(delay) AS delay, AVG(delay_std) AS delay_std, AVG(delay_var) AS delay_var, AVG(jitter) AS jitter, AVG(jitter_std) AS jitter_std, AVG(jitter_var) AS jitter_var, AVG(loss_rate) AS loss_rate, SUM(fail) AS fail, SUM(total) AS total FROM record_hour_tracert rht WHERE rht.record_date BETWEEN #{ava_start} AND #{ava_terminal}
AND rht.probe_id = #{probe_id}
AND rht.city_id = #{city_id}
AND rht.county_id = #{county_id}
AND record_time BETWEEN #{startTime} AND #{terminalTime}
GROUP BY service_type,target_id)t LEFT JOIN city ON t.city_id = city.city_id LEFT JOIN county ON t.county_id = county.county_id INNER JOIN probe ON t.probe_id = probe.id INNER JOIN target ON t.target_id = target.id
order by ${sidx} ${order}
limit #{offset}, #{limit}

select rht.id,rht.city_id,rht.county_id,rht.probe_id,rht.port,rht.task_id,rht.service_type,rht.target_id,rht.target_ip,rht.record_date,rht.remark, city.city_name AS city_name, county.county_name AS area_name, probe.probe_name AS probe_name, target.target_name AS target_name, time_format(record_time, '%H') AS record_time, AVG(rht.delay) AS delay, AVG(rht.delay_std) AS delay_std, AVG(rht.delay_var) AS delay_var, AVG(rht.jitter) AS jitter, AVG(rht.jitter_std) AS jitter_std, AVG(rht.jitter_var) AS jitter_var, AVG(rht.loss_rate) AS loss_rate, SUM(rht.fail) AS fail, SUM(rht.total) AS total FROM record_day_tracert rht LEFT JOIN city ON rht.city_id = city.city_id LEFT JOIN county ON rht.county_id = county.county_id INNER JOIN probe ON rht.probe_id = probe.id INNER JOIN target ON rht.target_id = target.id WHERE rht.record_date BETWEEN #{ava_start} AND #{ava_terminal}
AND rht.city_id = #{city_id}
AND rht.county_id = #{county_id}
AND rht.target_id = #{target_id}
AND rht.probe_id = #{probe_id}
GROUP BY rht.city_id,rht.county_id,rht.service_type,rht.probe_id,date_format(rht.record_date,'%Y-%m-%d')

select rhp.id,rhp.probe_id,rhp.port,rhp.task_id,rhp.service_type,rhp.target_id,rhp.target_ip,rhp.record_date,rhp.remark, probe.probe_name AS probe_name, task.task_name AS task_name, target.target_name AS target_name, target.value AS targetip_name, floor(time_format(record_time, '%H')/#{interval}) AS rt, concat('从',floor(time_format(record_time, '%H')/#{interval})*#{interval},'点至',floor(time_format(record_time, '%H')/#{interval})*#{interval}+#{interval},'点') AS record_time, round(AVG(rhp.delay),2) AS delay, round(AVG(rhp.delay_std),2) AS delay_std, round(AVG(rhp.delay_var),2) AS delay_var, round(AVG(rhp.jitter),2) AS jitter, round(AVG(rhp.jitter_std),2) AS jitter_std, round(AVG(rhp.jitter_var),2) AS jitter_var, round(AVG(rhp.loss_rate),2) AS loss_rate, d.data_name AS servicetype_name FROM record_hour_tracert rhp INNER JOIN probe ON rhp.probe_id = probe.id INNER JOIN task ON rhp.task_id = task.id INNER JOIN target ON rhp.target_id = target.id LEFT JOIN dic_data d ON (d.dic_type_id = 5 AND rhp.service_type = d.data_id) WHERE rhp.service_type = #{service_type} AND rhp.record_date BETWEEN #{startDate} AND #{terminalDate} AND rhp.record_time BETWEEN #{start_time} AND #{end_time}
AND rhp.task_id = #{task_id}
AND rhp.target_id = #{target_id}
AND rhp.probe_id = #{probe_id}
GROUP BY rhp.service_type,rhp.target_id,rhp.task_id,rhp.probe_id,date_format(rhp.record_date,'%Y-%m-%d'),rt
ORDER BY ${sidx} ${order}
ORDER BY record_date DESC,rt DESC
limit #{offset}, #{limit}

SELECT count(*) FROM (
select
rhp.id,rhp.probe_id,rhp.port,rhp.task_id,rhp.service_type,rhp.target_id,rhp.target_ip,rhp.record_date,rhp.remark,
floor(time_format(record_time, '%H')/#{interval}) AS rt
INNER JOIN probe
ON rhp.probe_id = probe.id
INNER JOIN task
ON rhp.task_id = task.id
INNER JOIN target
ON rhp.target_id = target.id
FROM record_hour_tracert rhp
WHERE
rhp.service_type = #{service_type}
AND rhp.record_date BETWEEN #{startDate} AND #{terminalDate}
AND rhp.record_time BETWEEN #{start_time} AND #{end_time}

	AND rhp.task_id = #{task_id}


	AND rhp.target_id = #{target_id}


	AND rhp.probe_id = #{probe_id}

GROUP BY
rhp.service_type,rhp.target_id,rhp.task_id,rhp.probe_id,date_format(rhp.record_date,'%Y-%m-%d'),rt) t
