mapper.select:
		select * from record_hour_web_page


                order by ${sidx} ${order}


                order by id desc



			limit #{offset}, #{limit}

select record_hour_web_page.id,record_hour_web_page.city_id,record_hour_web_page.county_id,record_hour_web_page.probe_id,record_hour_web_page.port,record_hour_web_page.task_id,record_hour_web_page.service_type,record_hour_web_page.target_id,record_hour_web_page.target_ip,record_hour_web_page.record_date,record_hour_web_page.record_time,record_hour_web_page.remark, city.city_name AS city_name, county.county_name AS area_name, probe.probe_name AS probe_name, target.target_name AS target_name, AVG(record_hour_web_page.dns_delay) AS dns_delay, AVG(record_hour_web_page.conn_delay) AS conn_delay, AVG(record_hour_web_page.headbyte_delay) AS headbyte_delay, AVG(record_hour_web_page.page_file_delay) AS page_file_delay, AVG(record_hour_web_page.redirect_delay) AS redirect_delay, AVG(record_hour_web_page.above_fold_delay) AS above_fold_delay, AVG(record_hour_web_page.page_element_delay) AS page_element_delay, AVG(record_hour_web_page.download_rate) AS download_rate, SUM(record_hour_web_page.fail) AS fail, SUM(record_hour_web_page.total) AS total FROM record_hour_web_page LEFT JOIN city ON record_hour_web_page.city_id = city.city_id LEFT JOIN county ON record_hour_web_page.county_id = county.county_id INNER JOIN probe ON record_hour_web_page.probe_id = probe.id LEFT JOIN target ON record_hour_web_page.target_id = target.id WHERE record_hour_web_page.record_date BETWEEN #{ava_start} AND #{ava_terminal}
AND record_hour_web_page.city_id = #{city_id}
AND record_hour_web_page.county_id = #{county_id}
AND record_hour_web_page.target_id = #{target_id}
AND record_hour_web_page.probe_id = #{probe_id}
GROUP BY record_hour_web_page.city_id,record_hour_web_page.county_id,record_hour_web_page.service_type,record_hour_web_page.target_id,record_hour_web_page.probe_id,date_format(record_hour_web_page.record_date,'%Y-%m-%d'),time_format(record_hour_web_page.record_time, '%H')

select webp.id,webp.city_id,webp.county_id,webp.probe_id,webp.port,webp.task_id,webp.service_type,webp.target_id,webp.target_ip,webp.record_date,webp.record_time,webp.remark, city.city_name AS city_name, county.county_name AS area_name, probe.probe_name AS probe_name, target.target_name AS target_name, AVG(webp.dns_delay) AS dns_delay, AVG(webp.conn_delay) AS conn_delay, AVG(webp.headbyte_delay) AS headbyte_delay, AVG(webp.page_file_delay) AS page_file_delay, AVG(webp.redirect_delay) AS redirect_delay, AVG(webp.above_fold_delay) AS above_fold_delay, AVG(webp.page_element_delay) AS page_element_delay, AVG(webp.download_rate) AS download_rate, SUM(webp.fail) AS fail, SUM(webp.total) AS total FROM record_day_web_page webp LEFT JOIN city ON webp.city_id = city.city_id LEFT JOIN county ON webp.county_id = county.county_id INNER JOIN probe ON webp.probe_id = probe.id LEFT JOIN target ON webp.target_id = target.id WHERE webp.record_date BETWEEN #{ava_start} AND #{ava_terminal}
AND webp.city_id = #{city_id}
AND webp.county_id = #{county_id}
AND webp.target_id = #{target_id}
AND webp.probe_id = #{probe_id}
GROUP BY webp.city_id,webp.county_id,webp.service_type,webp.target_id,webp.probe_id,date_format(webp.record_date,'%Y-%m-%d'),time_format(webp.record_time, '%H')

select rhp.id,rhp.probe_id,rhp.port,rhp.task_id,rhp.service_type,rhp.target_id,rhp.target_ip,rhp.record_date,rhp.remark, probe.probe_name AS probe_name, task.task_name AS task_name, target.target_name AS target_name, target.value AS targetip_name, floor(time_format(record_time, '%H')/#{interval}) AS rt, concat('从',floor(time_format(record_time, '%H')/#{interval})*#{interval},'点至',floor(time_format(record_time, '%H')/#{interval})*#{interval}+#{interval},'点') AS record_time, round(AVG(rhp.delay),2) AS delay, round(AVG(rhp.delay_std),2) AS delay_std, round(AVG(rhp.delay_var),2) AS delay_var, round(AVG(rhp.jitter),2) AS jitter, round(AVG(rhp.jitter_std),2) AS jitter_std, round(AVG(rhp.jitter_var),2) AS jitter_var, round(AVG(rhp.loss_rate),2) AS loss_rate, d.data_name AS servicetype_name FROM record_hour_web_page rhp INNER JOIN probe ON rhp.probe_id = probe.id INNER JOIN task ON rhp.task_id = task.id INNER JOIN target ON rhp.target_id = target.id LEFT JOIN dic_data d ON (d.dic_type_id = 5 AND rhp.service_type = d.data_id) WHERE rhp.service_type = #{service_type} AND rhp.record_date BETWEEN #{startDate} AND #{terminalDate} AND rhp.record_time BETWEEN #{start_time} AND #{end_time}
AND rhp.task_id = #{task_id}
AND rhp.target_id = #{target_id}
AND rhp.probe_id = #{probe_id}
GROUP BY rhp.service_type,rhp.target_id,rhp.task_id,rhp.probe_id,date_format(rhp.record_date,'%Y-%m-%d'),rt
ORDER BY ${sidx} ${order}
ORDER BY record_date DESC,rt DESC
limit #{offset}, #{limit}

SELECT count(*) FROM (
select
rhp.id,rhp.probe_id,rhp.port,rhp.task_id,rhp.service_type,rhp.target_id,rhp.target_ip,rhp.record_date,rhp.remark,
floor(time_format(record_time, '%H')/#{interval}) AS rt,AVG(rhp.delay) AS delay,
AVG(rhp.delay_std) AS delay_std,
AVG(rhp.delay_var) AS delay_var,
AVG(rhp.jitter) AS jitter,
AVG(rhp.jitter_std) AS jitter_std,
AVG(rhp.jitter_var) AS jitter_var,
AVG(rhp.loss_rate) AS loss_rate
FROM record_hour_web_page rhp
WHERE
rhp.service_type = #{service_type}
AND rhp.record_date BETWEEN #{startDate} AND #{terminalDate}
AND rhp.record_time BETWEEN #{start_time} AND #{end_time}

	AND rhp.task_id = #{task_id}


	AND rhp.target_id = #{target_id}


	AND rhp.probe_id = #{probe_id}

GROUP BY
rhp.service_type,rhp.target_id,rhp.task_id,rhp.probe_id,date_format(rhp.record_date,'%Y-%m-%d'),rt) t
