select webv.id,webv.city_id,webv.county_id,webv.probe_id,webv.port,webv.task_id,webv.service_type,webv.target_id,webv.target_ip,webv.record_date,webv.remark, city.city_name AS city_name, county.county_name AS area_name, probe.probe_name AS probe_name, target.target_name AS target_name, probe.access_layer AS accessLayer, time_format(record_time, '%H') AS record_time, AVG(webv.dns_delay) AS dns_delay, AVG(webv.ws_conn_delay) AS ws_conn_delay, AVG(webv.web_page_delay) AS web_page_delay, AVG(webv.head_frame_delay) AS head_frame_delay, AVG(webv.init_buffer_delay) AS init_buffer_delay, AVG(webv.load_delay) AS load_delay, AVG(webv.total_buffer_delay) AS total_buffer_delay, AVG(webv.download_rate) AS download_rate, AVG(webv.buffer_time) AS buffer_time, SUM(webv.fail) AS fail, SUM(webv.total) AS total FROM record_day_web_video webv LEFT JOIN city ON webv.city_id = city.city_id LEFT JOIN county ON webv.county_id = county.county_id INNER JOIN probe ON webv.probe_id = probe.id LEFT JOIN target ON webv.target_id = target.id WHERE webv.record_date BETWEEN #{ava_start} AND #{ava_terminal}
AND webv.city_id = #{city_id}
AND webv.county_id = #{county_id}
AND webv.target_id = #{target_id}
AND webv.probe_id = #{probe_id}
GROUP BY webv.city_id,webv.county_id,webv.service_type,webv.probe_id,date_format(webv.record_date,'%Y-%m-%d'),time_format(webv.record_time, '%H')

select rhp.id,rhp.probe_id,rhp.port,rhp.task_id,rhp.service_type,rhp.target_id,rhp.target_ip,rhp.record_date,rhp.remark, probe.probe_name AS probe_name, task.task_name AS task_name, target.target_name AS target_name, target.value AS targetip_name, floor(time_format(record_time, '%H')/#{interval}) AS rt, concat('从',floor(time_format(record_time, '%H')/#{interval})*#{interval},'点至',floor(time_format(record_time, '%H')/#{interval})*#{interval}+#{interval},'点') AS record_time, round(AVG(rhp.dns_delay),2) AS dns_delay, round(AVG(rhp.ws_conn_delay),2) AS ws_conn_delay, round(AVG(rhp.web_page_delay),2) AS web_page_delay, round(AVG(rhp.head_frame_delay),2) AS head_frame_delay, round(AVG(rhp.init_buffer_delay),2) AS init_buffer_delay, round(AVG(rhp.load_delay),2) AS load_delay, round(AVG(rhp.total_buffer_delay),2) AS total_buffer_delay, round(AVG(rhp.download_rate),2) AS download_rate, round(AVG(rhp.buffer_time),2) AS buffer_time, d.data_name AS servicetype_name FROM record_hour_web_video rhp INNER JOIN probe ON rhp.probe_id = probe.id INNER JOIN task ON rhp.task_id = task.id INNER JOIN target ON rhp.target_id = target.id LEFT JOIN dic_data d ON (d.dic_type_id = 5 AND rhp.service_type = d.data_id) WHERE rhp.service_type = #{service_type} AND rhp.record_date BETWEEN #{startDate} AND #{terminalDate} AND rhp.record_time BETWEEN #{start_time} AND #{end_time}
AND rhp.task_id = #{task_id}
AND rhp.target_id = #{target_id}
AND rhp.probe_id = #{probe_id}
GROUP BY rhp.service_type,rhp.target_id,rhp.task_id,rhp.probe_id,date_format(rhp.record_date,'%Y-%m-%d'),rt
ORDER BY ${sidx} ${order}
ORDER BY record_date DESC,rt DESC
limit #{offset}, #{limit}

SELECT count(*) FROM (
select
rhp.id,rhp.probe_id,rhp.port,rhp.task_id,rhp.service_type,rhp.target_id,rhp.target_ip,rhp.record_date,rhp.remark,
floor(time_format(record_time, '%H')/#{interval}) AS rt
FROM record_hour_web_video rhp
INNER JOIN probe
ON rhp.probe_id = probe.id
INNER JOIN task
ON rhp.task_id = task.id
INNER JOIN target
ON rhp.target_id = target.id
WHERE
rhp.service_type = #{service_type}
AND rhp.record_date BETWEEN #{startDate} AND #{terminalDate}
AND rhp.record_time BETWEEN #{start_time} AND #{end_time}

	AND rhp.task_id = #{task_id}


	AND rhp.target_id = #{target_id}


	AND rhp.probe_id = #{probe_id}

GROUP BY
rhp.service_type,rhp.target_id,rhp.task_id,rhp.probe_id,date_format(rhp.record_date,'%Y-%m-%d'),rt) t
