mapper.select:
		select * from record_web_page


                order by ${sidx} ${order}


                order by id desc



			limit #{offset}, #{limit}

select record_web_page.*, probe.probe_name AS probe_name, target.target_name AS target_name, target.value AS targetip_name FROM record_web_page LEFT JOIN probe ON record_web_page.probe_id =probe.id LEFT JOIN target ON record_web_page.target_id = target.id WHERE record_web_page.dispatch_id = #{dispatch_id}
order by ${sidx} ${order}
order by id desc
limit #{offset}, #{limit}

select record_web_page.*, probe.probe_name AS probe_name, task.task_name AS task_name, target.target_name AS target_name, target.value AS targetip_name, a.data_name AS tasktype_name, b.data_name AS targettype_name, c.data_name AS state_name, d.data_name AS servicetype_name FROM record_web_page INNER JOIN probe ON record_web_page.probe_id =probe.id INNER JOIN task ON record_web_page.task_id = task.id AND task.is_ondemand =0 INNER JOIN target ON record_web_page.target_id = target.id LEFT JOIN dic_data a ON (a.dic_type_id = 6 AND record_web_page.task_type = a.data_id) LEFT JOIN dic_data b ON (b.dic_type_id = 8 AND record_web_page.target_type = b.data_id) LEFT JOIN dic_data c ON (c.dic_type_id = 9 AND record_web_page.state = c.data_id) LEFT JOIN dic_data d ON (d.dic_type_id = 5 AND record_web_page.service_type = d.data_id) WHERE record_web_page.service_type = #{service_type}
AND task_id = #{task_id}
AND target_id = #{target_id}
AND probe_id = #{probe_id}
AND record_date BETWEEN #{startDate} AND #{terminalDate}
AND record_time BETWEEN #{start_time} AND #{end_time}
order by ${sidx} ${order}
order by record_date desc,record_time desc
limit #{offset}, #{limit}

select count(*) FROM record_web_page
INNER JOIN probe
ON  record_web_page.probe_id =probe.id
INNER JOIN task
ON record_web_page.task_id = task.id
AND task.is_ondemand =0
INNER JOIN target
ON record_web_page.target_id = target.id
WHERE
record_web_page.service_type = #{service_type}
AND
record_date BETWEEN #{startDate} AND #{terminalDate}

	AND task_id = #{task_id}


	AND target_id = #{target_id}


	AND probe_id = #{probe_id}


	AND record_time BETWEEN #{start_time} AND #{end_time}
